j_amount = 0
b_amount = 0
if frappe.db.exists({"doctype": "ORC List", "name": doc.orc_id}):
    orc = doc.orc_id
    if frappe.db.exists({"doctype": "Journal Entry", "orc_id": doc.orc_id}):
        # frappe.msgprint("exists")
        orc_data = frappe.db.sql(f"""SELECT sub_total FROM `tabORC List` tol where tol.name= '{orc}' ;""", as_dict=1)
        je_data = frappe.db.sql(f"""SELECT total_debit, docstatus FROM `tabJournal Entry` tje where tje.orc_id = '{orc}' and tje.docstatus != '{2}' ;""", as_dict=1)
        # frappe.msgprint(len(je_data))
        if len(je_data) < 1:
            j_amount = orc_data[0].sub_total
        else:
            for each in je_data:
                if each.docstatus != 2:
                    j_amount = j_amount + each.total_debit

        data = frappe.get_doc('ORC List', orc)

        b_amount = orc_data[0].sub_total - j_amount
        data.balance_amount = b_amount
        data.save()

for d in doc.items:
    if d.opportunity_type == "Renewal":
        if d.renewal_id != " ":
            old_data = frappe.get_doc('Renewal List',d.renewal_id)
            old_data.status = "Cofed"
            old_data.save()
        else:
            frappe.throw(f"Please check Renewal ID")

if doc.items:
    for each in doc.items:
        if each.ord_id:
            orc_data = frappe.get_doc('ORC List',each.orc_id)
            orc_data.status = "Waiting For Payment"
            orc_data.workflow_state="Waiting For Payment"
            orc_data.save()
            frappe.msgprint("ORC Status Changed")

if doc.references:
    for each in doc.references:
        if each.reference_doctype == "Sales Invoice" and each.reference_name:
            orc_list = []
            # frappe.msgprint("entered")
            sales_invoice = frappe.get_doc('Sales Invoice',each.reference_name)

            data = sales_invoice.grand_total
            # frappe.msgprint(data)
            item_data = frappe.db.sql(f"""SELECT name,orc_id FROM `tabSales Invoice Item` WHERE  parent='{each.reference_name}'  ;""",as_dict=1)

            for i in item_data:
                if i.orc_id:
                    if i.orc_id in orc_list:
                        pass
                    else:
                        orc_list.append(i.orc_id)
            # frappe.msgprint(invoice_data[0][1])
            if len(orc_list) >0 and sales_invoice.outstanding_amount == 0:
                for r in orc_list:
                    orc_data = frappe.get_doc('ORC List',r)
                    orc_data.status = "Payout"
                    orc_data.workflow_state="Payout"
                    orc_data.save()
                    frappe.msgprint("ORC Status Changed")
